// No this file isnt actually a bad joke
// Big thanks to eddmann.com for this vector template thing
// Essentially this is an overcomplicated header to implement pointers into C
// Because stdlib.h doesn't have nice features like this

#ifndef VECTOR_H
// essentially a bunch of macros that aren't required but are there for easily implementing a vector
#define VECTOR_H

#define VECTOR_INIT_CAPACITY 4

#define VECTOR_INIT(vec) vector vec; vector_init(&vec)
#define VECTOR_ADD(vec, item) vector_add(&vec, (void *) item)
#define VECTOR_SET(vec, id, item) vector_set(&vec, id, (void *) item)
#define VECTOR_GET(vec, type, id) (type) vector_get(&vec, id)
#define VECTOR_DEL(vec, id) vector_del(&vec, id)
#define VECTOR_TOTAL(vec) vector_total(&vec)
#define VECTOR_FREE(vec) vector_free(&vec)

typedef struct vector
{
	void **items;
	int capacity;
	int total;
} vector;

void vector_init(vector *); // function to initialize vector
int vector_total(vector *); // function to get the vector total
static void vector_resize(vector *, int); // static function to resize said vector
void vector_add(vector *, void *); // function to push to the vector
void vector_set(vector *, int, void *); // function to set the vector
void *vector_get(vector *, int); // function that is a pointer to get value in vector
void vector_del(vector *, int); // function to delete element of the vector
void vector_free(vector *); //function to free the vector

#endif

